mapper.select:
SELECT DISTINCT HEADHUNTPOST.HEADHUNTPOST_id, MEMBER.MEMBER_nickname, HEADHUNTPOST.HEADHUNTPOST_title, HEADHUNTPOST.HEADHUNTPOST_content, HEADHUNTPOST.HEADHUNTPOST_date, HEADHUNTPOST.HEADHUNTPOST_viewcnt, ( SELECT COUNT(*) FROM RECOMMEND WHERE RECOMMEND.POST_id = HEADHUNTPOST.HEADHUNTPOST_id ) AS RECOMMEND_cnt, ( SELECT POSTIMG.POSTIMG_name FROM POSTIMG WHERE POSTIMG.POST_id = HEADHUNTPOST.HEADHUNTPOST_id ORDER BY POSTIMG.POSTIMG_id ASC LIMIT 1 ) AS POSTIMG_name FROM HEADHUNTPOST INNER JOIN MEMBER ON HEADHUNTPOST.MEMBER_id = MEMBER.MEMBER_id LEFT JOIN RECOMMEND ON HEADHUNTPOST.HEADHUNTPOST_id = RECOMMEND.POST_id WHERE 1=1
AND HEADHUNTPOST.HEADHUNTPOST_title LIKE CONCAT('%', #{searchInput}, '%')
AND HEADHUNTPOST.HEADHUNTPOST_content LIKE CONCAT('%', #{searchInput}, '%')
AND MEMBER.MEMBER_nickname LIKE CONCAT('%', #{searchInput}, '%')
AND (HEADHUNTPOST.HEADHUNTPOST_title LIKE CONCAT('%', #{searchInput}, '%') OR HEADHUNTPOST.HEADHUNTPOST_content LIKE CONCAT('%', #{searchInput}, '%'))
AND HEADHUNTPOST_role LIKE CONCAT('%',#{headHuntPostRole},'%')
AND HEADHUNTPOST_region LIKE CONCAT('%',#{headHuntPostRegion},'%')
AND HEADHUNTPOST_concept LIKE CONCAT('%',#{headHuntPostConcept},'%')
AND HEADHUNTPOST_pay BETWEEN #{minPay} AND #{maxPay}
AND HEADHUNTPOST_workDate BETWEEN #{startWorkDate} AND #{endWorkDate}
AND DATE(HEADHUNTPOST_date) BETWEEN #{fromday} AND #{today}
ORDER BY HEADHUNTPOST.HEADHUNTPOST_date ${sortOrder == 'asc' ? 'ASC' : 'DESC'}
